import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import aboutImg from '../assets/about.png';

export default function About() {
  return (
    <section id="about" style={{ background: '#fff', padding: '80px 0' }}>
      <Container>
        {/* Heading */}
        <Row className="mb-4">
          <Col>
            <p
              style={{
                textTransform: 'uppercase',
                color: '#666',
                fontSize: '0.85rem',
                marginBottom: '8px',
                letterSpacing: '1px',
              }}
            >
              ABOUT CAVOLA
            </p>
            <h2
              style={{
                fontWeight: '700',
                color: '#154734',
                fontSize: '2.2rem',
                lineHeight: '1.2',
                marginBottom: '0',
              }}
            >
              Your reliable partner in African<br />
              Agro-commodity trade
            </h2>
          </Col>
        </Row>

        {/* Green full-width box with text + pop-out image */}
        <Row
          className="align-items-center mb-5"
          style={{
            background: '#154734',
            borderRadius: '12px',
            overflow: 'visible',
            position: 'relative',
            minHeight: '320px',
          }}
        >
          {/* Text */}
          <Col
            md={6}
            className="p-5"
            style={{
              color: '#fff',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              minHeight: '320px',
            }}
          >
            <p style={{ fontSize: '1.1rem', lineHeight: '1.7', marginBottom: '28px', marginTop: '8px' }}>
              At Cavola West Africa Limited, we help importers, manufacturers, and partners secure high-quality agricultural products without the usual risks. From Nigeria’s farms to international buyers, we ensure compliance, consistency, and efficiency.
            </p>
            <Button
              style={{
                background: '#7CB342',
                color: '#fff',
                border: 'none',
                borderRadius: '999px',
                padding: '12px 28px',
                fontWeight: 600,
                fontSize: '1rem',
                boxShadow: '0 2px 8px rgba(0,0,0,0.04)',
                width: 'fit-content',
              }}
            >
              Explore Our Products <span style={{ fontSize: '1.2em', marginLeft: '6px' }}>→</span>
            </Button>
          </Col>

          {/* Image */}
          <Col
            md={6}
            className="position-relative d-flex justify-content-center align-items-start"
            style={{
              minHeight: '320px',
              padding: 0,
            }}
          >
            <img
              src={aboutImg}
              alt="About Cavola"
              style={{
                width: '100%',
                maxWidth: '400px',
                height: '480px',
                marginTop: '-220px', // pushed image further up
                marginRight: '-40px',
                zIndex: 2,
                borderRadius: '0',
                objectFit: 'cover',
              }}
            />
          </Col>
        </Row>
      </Container>
    </section>
  );
}
