import React from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { useNavigate, useLocation } from 'react-router-dom';
import logo from '../assets/logo.png'; // <-- Make sure logo.png is in src/assets/

export default function NavbarMain() {
  const navigate = useNavigate();
  const location = useLocation();

  const handleScroll = (e, id) => {
    e.preventDefault();
    const target = document.getElementById(id);
    if (location.pathname !== '/') {
      navigate('/');
      setTimeout(() => {
        document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
      }, 150);
    } else {
      target?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <Navbar bg="white" expand="lg" fixed="top" className="shadow-sm py-2">
      <Container className="d-flex justify-content-between align-items-center">
        {/* Logo */}
        <Navbar.Brand href="/" className="d-flex align-items-center">
          <img
            src={logo}
            alt="Cavola Logo"
            style={{ height: '147.804px;', marginRight: '8px' }}
          />
        </Navbar.Brand>

        {/* Centered Nav Menu */}
        <div
          className="d-none d-lg-flex px-4 py-2"
          style={{
            background: '#f8f8f8',
            borderRadius: '999px',
          }}
        >
          <Nav className="mx-auto">
            <Nav.Link
              href="#about"
              onClick={(e) => handleScroll(e, 'about')}
              className="px-3"
            >
              About Us
            </Nav.Link>
            <Nav.Link
              href="#services"
              onClick={(e) => handleScroll(e, 'services')}
              className="px-3"
            >
              Services
            </Nav.Link>
            <Nav.Link
              href="#blog"
              onClick={(e) => handleScroll(e, 'blog')}
              className="px-3"
            >
              Blog
            </Nav.Link>
            <Nav.Link
              href="#contact"
              onClick={(e) => handleScroll(e, 'contact')}
              className="px-3"
            >
              Contact Us
            </Nav.Link>
          </Nav>
        </div>

        {/* Right CTA */}
        <Button
          className="d-none d-lg-inline-block"
          style={{
            background: '#7CB342',
            border: 'none',
            borderRadius: '999px',
            padding: '8px 20px',
          }}
          onClick={() => handleScroll(new Event('click'), 'contact')}
        >
          Request a Quote
        </Button>
      </Container>
    </Navbar>
  );
}
