import React, { useEffect, useState } from 'react';
import { Carousel, Container, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

import hero1 from '../assets/hero1.png';
import hero2 from '../assets/hero2.png';
import hero3 from '../assets/hero3.png';
import hero4 from '../assets/hero4.png';
import hero5 from '../assets/hero5.png';

export default function Hero() {
  const images = [
    { src: hero1, caption: 'Cocoa' },
    { src: hero2, caption: 'Coffee' },
    { src: hero3, caption: 'Sesame' },
    { src: hero4, caption: 'Hibiscus' },
    { src: hero5, caption: 'Chilli Pepper' },
  ];

  const navigate = useNavigate();

  // For smooth fade-in on the right text
  const [loaded, setLoaded] = useState(false);
  useEffect(() => {
    setTimeout(() => setLoaded(true), 200); // slight delay for smoothness
  }, []);

  return (
    <section
      className="hero"
      id="home"
      style={{
        background: '#fff',
        padding: '20px 0 80px 0',
      }}
    >
      <Container className="hero-container">
  <div className="row align-items-center">
    {/* Left slideshow */}
    <div className="col-lg-6 col-md-12 mb-4">
      <Carousel
        controls={false}
        indicators
        interval={2000}
        fade
        ride="carousel"
        pause={false}
      >
        {images.map((img, i) => (
          <Carousel.Item key={i}>
            <img
              src={img.src}
              alt={img.caption}
              className="d-block w-100 hero-img"
            />
            <p className="hero-caption">{img.caption}</p>
          </Carousel.Item>
        ))}
      </Carousel>
    </div>

    {/* Right text block */}
    <div className="col-lg-6 col-md-12 text-block">
      <h1>
        <span className="text-primary">Finding </span>
        <span className="text-accent">reliable agro-commodity suppliers</span>
        <span className="text-primary">
          {' '}in West Africa shouldnâ€™t be difficult.
        </span>
      </h1>

      <p>
        Inconsistent quality, weak logistics, and compliance risks cost
        buyers time and money. Cavola solves this by delivering trusted,
        certified agro-commodities and petrochemicals from Nigeria to global
        markets.
      </p>

      <div className="btn-group">
        <Button className="btn-cta">Request a Quote</Button>
        <Button className="btn-outline-cta">Book a Call with Us</Button>
      </div>
    </div>
  </div>
</Container>

    </section>
  );
}
