import React from 'react';
import { Container } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import useStore from '../store/useStore';

export default function BlogDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const blogs = useStore(state => state.blogs);
  const blog = blogs.find(b => b.id === id);

  if (!blog) {
    return (
      <Container className="container-narrow section">
        <h2>Blog Not Found</h2>
        <p>We couldn't find the blog you were looking for.</p>
        <p><a href="#" onClick={(e)=>{e.preventDefault(); navigate('/blog')}}>Back to blog list</a></p>
      </Container>
    );
  }

  return (
    <section className="section">
      <Container className="container-narrow">
        <h2>{blog.title}</h2>
        <p className="text-muted">{blog.date} â€” {blog.author}</p>
        <img src={blog.image} alt={blog.title} style={{width:'100%', maxHeight:400, objectFit:'cover', borderRadius:8}} />
        <div style={{marginTop:20, whiteSpace: 'pre-line'}}>{blog.content}</div>
      </Container>
    </section>
  );
}
