// src/components/NavbarMain.jsx
import React from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { useNavigate, useLocation } from 'react-router-dom';
import logo from '../assets/logo.png';

export default function NavbarMain() {
  const navigate = useNavigate();
  const location = useLocation();

  // Scroll handler (for homepage sections only)
  const handleScrollTo = (e, id) => {
    e.preventDefault();
    const target = document.getElementById(id);
    if (location.pathname !== '/') {
      navigate('/');
      setTimeout(() => {
        document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
      }, 150);
    } else {
      target?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div style={{ margin: 0, padding: 0 }}> {/* ✅ wrapper with no margin/padding */}
      <Navbar
        bg="white"
        expand="lg"
        className="m-0 p-0" // ✅ Bootstrap utility to force no margin/padding
        style={{
          borderBottom: 'none',
          boxShadow: 'none',
          margin: 0,
          padding: 0,
          display: 'flex',
          alignItems: 'center',
        }}
      >
        <Container
          fluid
          className="m-0 p-0" // ✅ force no spacing inside container
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            padding: '0 64px',
            margin: 0,
          }}
        >
          {/* Logo */}
          <Navbar.Brand
            onClick={() => navigate('/')}
            className="m-0 p-0"
            style={{
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              flexShrink: 0,
              margin: 0,
              padding: 0,
            }}
          >
            <img
              src={logo}
              alt="Cavola Logo"
              style={{
                height: '220px',
                width: 'auto',
                objectFit: 'contain',
                display: 'block',
              }}
            />
          </Navbar.Brand>

          {/* Hamburger Toggle (mobile) */}
          <Navbar.Toggle aria-controls="navbar-nav" className="m-0 p-0" />

          {/* Menu + Button */}
          <Navbar.Collapse id="navbar-nav" className="justify-content-center m-0 p-0">
            <Nav
              className="mx-auto text-center"
              style={{
                display: 'flex',
                padding: '12px 30px',
                alignItems: 'center',
                gap: '64px',
                borderRadius: '24px',
                background: '#F3F3F3',
                margin: 0,
              }}
            >
              <Nav.Link
                onClick={(e) => handleScrollTo(e, 'about')}
                style={{ color: '#292121', fontSize: '22px', fontFamily: 'Raleway' }}
              >
                About Us
              </Nav.Link>
              <Nav.Link
                onClick={(e) => handleScrollTo(e, 'services')}
                style={{ color: '#292121', fontSize: '22px', fontFamily: 'Raleway' }}
              >
                Services
              </Nav.Link>
              <Nav.Link
                onClick={() => navigate('/blog')}
                style={{ color: '#292121', fontSize: '22px', fontFamily: 'Raleway' }}
              >
                Blog
              </Nav.Link>
              <Nav.Link
                onClick={() => navigate('/contact')}
                style={{ color: '#292121', fontSize: '22px', fontFamily: 'Raleway' }}
              >
                Contact Us
              </Nav.Link>
            </Nav>

            {/* CTA Button */}
            <div className="text-center mt-3 mt-lg-0 m-0 p-0">
              <Button
                style={{
                  color: '#FFF',
                  fontSize: '20px',
                  fontFamily: 'Raleway',
                  display: 'flex',
                  width: '220px',
                  padding: '6px 0',
                  justifyContent: 'center',
                  alignItems: 'center',
                  borderRadius: '25px',
                  background: '#86AD43',
                  border: 'none',
                }}
                onClick={() => navigate('/contact')}
              >
                Request a Quote
              </Button>
            </div>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </div>
  );
}
