import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import useStore from "../store/useStore";
import BlogCard from "./BlogCard";
import bannerImg from "../assets/hero2.png";

export default function BlogList() {
  const blogs = useStore((state) => state.blogs);
  const navigate = useNavigate();

  // Pagination setup
  const blogsPerPage = 9; // ✅ now showing 9 per page
  const [currentPage, setCurrentPage] = useState(1);

  const indexOfLast = currentPage * blogsPerPage;
  const indexOfFirst = indexOfLast - blogsPerPage;
  const currentBlogs = blogs.slice(indexOfFirst, indexOfLast);

  const totalPages = Math.ceil(blogs.length / blogsPerPage);

  return (
    <section
      style={{
        width: "100%",
        minHeight: "100vh",
        borderRadius: "10px",
        background: "#094D12",
        margin: "0 auto",
        padding: "40px",
        color: "#fff",
      }}
    >
      <Container fluid>
        {/* Header Section */}
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            flexWrap: "wrap",
          }}
        >
          {/* Left text */}
          <h2
            style={{
              color: "#F7C35F",
              fontFamily: "Raleway",
              fontSize: "64px",
              fontWeight: 700,
              lineHeight: "61.68px",
              margin: 0,
            }}
          >
            Blog & Insights
          </h2>

          {/* Banner image */}
          <div
            style={{
              width: "100%",
              maxWidth: "885px",
              height: "400px",
              background: `url(${bannerImg}) center / cover no-repeat`,
              borderRadius: "10px",
              marginTop: "20px",
            }}
          />
        </div>

        {/* Blog Grid */}
        <Row xs={1} md={3} className="g-4 mt-5">
          {currentBlogs.map((blog) => (
            <Col key={blog.id} onClick={() => navigate(`/blog/${blog.id}`)}>
              <BlogCard blog={blog} />
            </Col>
          ))}
        </Row>

        {/* Pagination */}
        <div
          style={{
            marginTop: "40px",
            display: "flex",
            justifyContent: "center",
            gap: "10px",
          }}
        >
          {/* Prev */}
          <button
            onClick={() => setCurrentPage(currentPage - 1)}
            disabled={currentPage === 1}
            style={{
              padding: "10px 20px",
              borderRadius: "6px",
              border: "1px solid #C4C4C4",
              background: "transparent",
              color: "#fff",
              cursor: currentPage === 1 ? "not-allowed" : "pointer",
            }}
          >
            ← Prev
          </button>

          {/* Page numbers */}
          {[...Array(totalPages).keys()].map((num) => (
            <button
              key={num + 1}
              onClick={() => setCurrentPage(num + 1)}
              style={{
                width: "48px",
                height: "48px",
                borderRadius: "6px",
                border: "1px solid #C4C4C4",
                background:
                  currentPage === num + 1 ? "#246A21" : "transparent",
                color: "#fff",
                cursor: "pointer",
              }}
            >
              {num + 1}
            </button>
          ))}

          {/* Next */}
          <button
            onClick={() => setCurrentPage(currentPage + 1)}
            disabled={currentPage === totalPages}
            style={{
              padding: "10px 20px",
              borderRadius: "6px",
              background: "#246A21",
              color: "#fff",
              border: "none",
              cursor: currentPage === totalPages ? "not-allowed" : "pointer",
            }}
          >
            Next →
          </button>
        </div>
      </Container>
    </section>
  );
}
