import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import { useParams, useNavigate } from "react-router-dom";
import useStore from "../store/useStore";
import BlogCard from "./BlogCard";

export default function BlogDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const blogs = useStore((state) => state.blogs);
  const blog = blogs.find((b) => b.id === id);

  if (!blog) {
    return (
      <Container
        className="container-narrow section"
        style={{ padding: "100px 0", textAlign: "center" }}
      >
        <h2 style={{ fontWeight: "700" }}>Blog Not Found</h2>
        <p>We couldn’t find the blog you were looking for.</p>
        <p>
          <a
            href="#"
            onClick={(e) => {
              e.preventDefault();
              navigate("/blog");
            }}
            style={{ color: "#154734", fontWeight: "600" }}
          >
            ← Back to Blogs
          </a>
        </p>
      </Container>
    );
  }

  // Related blogs (exclude current one, take first 3 others)
  const relatedBlogs = blogs.filter((b) => b.id !== blog.id).slice(0, 3);

  return (
    <section className="section" style={{ background: "#fff", padding: "80px 0" }}>
      <Container style={{ maxWidth: "900px" }}>
        {/* Blog Image */}
        <div style={{ marginBottom: "40px" }}>
          <img
            src={blog.image}
            alt={blog.title}
            style={{
              width: "100%",
              height: "auto",
              maxHeight: "480px",
              objectFit: "cover",
              borderRadius: "12px",
              boxShadow: "0 6px 20px rgba(0,0,0,0.08)",
            }}
          />
        </div>

        {/* Blog Title + Meta */}
        <h1
          style={{
            fontSize: "2.2rem",
            fontWeight: "700",
            marginBottom: "15px",
            color: "#154734",
          }}
        >
          {blog.title}
        </h1>
        <p style={{ color: "#6c757d", fontSize: "1rem", marginBottom: "40px" }}>
          {blog.date} • {blog.author}
        </p>

        {/* Blog Content */}
        <div
          style={{
            fontSize: "1.1rem",
            lineHeight: "1.8",
            color: "#333",
            marginBottom: "60px",
            whiteSpace: "pre-line",
          }}
        >
          {blog.content}
        </div>

        {/* Related Blogs */}
        <h3
          style={{
            fontSize: "1.6rem",
            fontWeight: "700",
            marginBottom: "25px",
            color: "#154734",
          }}
        >
          Related Blogs
        </h3>
        <Row xs={1} md={3} className="g-4">
          {relatedBlogs.map((b) => (
            <Col key={b.id} onClick={() => navigate(`/blog/${b.id}`)}>
              <BlogCard blog={b} />
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
}
