import React from 'react';
import { Container, Button, Row, Col } from 'react-bootstrap';
import leadershipImg from '../assets/leadership.jpg';

export default function Leadership() {
  return (
    <section
      id="leadership"
      style={{
        background: '#094D12',
        padding: '80px 20px', // responsive spacing instead of fixed height
      }}
    >
      <Container>
        <Row className="align-items-center g-5">
          {/* Left Side */}
          <Col lg={6} md={12}>
            {/* Leadership & Governance */}
            <div
              style={{
                color: '#FFF',
                fontFamily: 'Raleway',
                fontSize: '18px',
                fontWeight: 400,
                textTransform: 'uppercase',
                marginBottom: '20px',
              }}
            >
              LEADERSHIP & GOVERNANCE
            </div>

            {/* Built on Experience */}
            <h2
              style={{
                color: '#F7C35F',
                fontFamily: 'Raleway',
                fontSize: '42px',
                fontWeight: 700,
                lineHeight: '1.3',
                marginBottom: '20px',
              }}
            >
              Built on experience
              <br />
              you can trust
            </h2>

            {/* Description */}
            <p
              style={{
                color: '#FFF',
                fontFamily: 'Raleway',
                fontSize: '18px',
                lineHeight: '1.6',
                marginBottom: '40px',
                maxWidth: '550px',
              }}
            >
              Our leadership team brings global experience in finance, agribusiness, and international trade.
            </p>

            {/* Spotlight Card */}
            <div
              style={{
                background: '#fff',
                borderRadius: '12px',
                padding: '30px',
                boxShadow: '0 2px 12px rgba(0,0,0,0.06)',
                marginTop: '40px',
              }}
            >
              <div
                style={{
                  color: '#86AD43',
                  fontFamily: 'Raleway',
                  fontSize: '22px',
                  fontWeight: 700,
                  marginBottom: '12px',
                }}
              >
                Spotlight
              </div>

              <div
                style={{
                  fontFamily: 'Raleway',
                  fontSize: '28px',
                  fontWeight: 700,
                  marginBottom: '16px',
                  color: '#1E1E1E',
                }}
              >
                Abolaji Olorunkoya –{' '}
                <span
                  style={{
                    color: '#86AD43',
                    fontSize: '20px',
                  }}
                >
                  Co-Founder & CEO
                </span>
              </div>

              <div
                style={{
                  color: '#1E1E1E',
                  fontFamily: 'Raleway',
                  fontSize: '18px',
                  lineHeight: '1.6',
                  marginBottom: '24px',
                }}
              >
                A seasoned finance executive with 20+ years of cross-continental experience, Abolaji bridges
                African farmers with global markets through ethical sourcing, innovation, and sustainable trade.
              </div>

              <Button
                style={{
                  borderRadius: '25px',
                  border: '1px solid #094D12',
                  background: '#fff',
                  color: '#094D12',
                  fontFamily: 'Raleway',
                  fontSize: '18px',
                  fontWeight: 700,
                  padding: '8px 24px',
                }}
              >
                Connect With Us
              </Button>
            </div>
          </Col>

          {/* Right Side - Image */}
          <Col lg={6} md={12}>
            <div
              style={{
                width: '100%',
                height: '100%',
                minHeight: '400px',
                borderRadius: '10px',
                background: `url(${leadershipImg}) center / cover no-repeat`,
              }}
            />
          </Col>
        </Row>
      </Container>
    </section>
  );
}
