import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Container, Row, Col } from "react-bootstrap";
import useStore from "../store/useStore";
import BlogCard from "./BlogCard";
import Services from "./Services"; // ✅ import services

export default function BlogDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const blogs = useStore((state) => state.blogs);

  const blog = blogs.find((b) => String(b.id) === id);

  if (!blog) {
    return (
      <Container style={{ padding: "60px 20px", textAlign: "center" }}>
        <h2>Blog not found</h2>
        <button
          onClick={() => navigate("/blog")}
          style={{
            marginTop: "20px",
            padding: "10px 20px",
            background: "#246A21",
            border: "none",
            borderRadius: "6px",
            color: "#fff",
            cursor: "pointer",
          }}
        >
          Back to Blogs
        </button>
      </Container>
    );
  }

  // Pick 3 related blogs (excluding the current one)
  const related = blogs.filter((b) => b.id !== blog.id).slice(0, 3);

  return (
    <>
      <section style={{ padding: "40px 0" }}>
        <Container>
          {/* Blog Header Image */}
          <div
            style={{
              width: "100%",
              maxHeight: "500px",
              overflow: "hidden",
              borderRadius: "10px",
              marginBottom: "30px",
            }}
          >
            <img
              src={blog.image}
              alt={blog.title}
              style={{ width: "100%", height: "100%", objectFit: "cover" }}
            />
          </div>

          {/* Blog Title */}
          <h1
            style={{
              fontFamily: "Raleway",
              fontSize: "32px",
              fontWeight: 700,
              marginBottom: "20px",
            }}
          >
            {blog.title}
          </h1>

          {/* Blog Content */}
          <p
            style={{
              fontFamily: "Raleway",
              fontSize: "18px",
              lineHeight: "1.6",
              color: "#333",
            }}
          >
            {blog.content || "Full blog content goes here..."}
          </p>

          {/* Related Blogs */}
          {related.length > 0 && (
            <div style={{ marginTop: "60px" }}>
              <h3
                style={{
                  fontSize: "24px",
                  fontWeight: 600,
                  marginBottom: "20px",
                }}
              >
                Related Blog
              </h3>
              <Row xs={1} sm={2} md={3} className="g-4">
                {related.map((b) => (
                  <Col key={b.id}>
                    <div
                      style={{ cursor: "pointer" }}
                      onClick={() => navigate(`/blog/${b.id}`)}
                    >
                      <BlogCard blog={b} />
                    </div>
                  </Col>
                ))}
              </Row>
            </div>
          )}
        </Container>
      </section>

      {/* ✅ Services Section (edge-to-edge like BlogList) */}
      <div style={{ marginTop: "80px", width: "100%" }}>
        <Services />
      </div>
    </>
  );
}
