import React, { useState } from "react";
import { Container, Row, Col, Pagination } from "react-bootstrap";
import useStore from "../store/useStore";
import BlogCard from "./BlogCard";

export default function BlogPage() {
  const blogs = useStore((state) => state.blogs);

  // Duplicate blogs for demo (so we have at least 9)
  const allBlogs = [...blogs, ...blogs, ...blogs];

  // Pagination setup
  const blogsPerPage = 9;
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = Math.ceil(allBlogs.length / blogsPerPage);

  const indexOfLastBlog = currentPage * blogsPerPage;
  const indexOfFirstBlog = indexOfLastBlog - blogsPerPage;
  const currentBlogs = allBlogs.slice(indexOfFirstBlog, indexOfLastBlog);

  const handlePageChange = (page) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  return (
    <section style={{ background: "#fff", padding: "60px 0" }}>
      <Container style={{ maxWidth: "1200px" }}>
        {/* Blog Banner */}
        <div
          style={{
            background: "#154734",
            color: "#fff",
            borderRadius: "12px",
            marginBottom: "50px",
            padding: "50px",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            gap: "30px",
          }}
        >
          <div>
            <h2 style={{ fontSize: "2.2rem", fontWeight: "700", marginBottom: "10px" }}>
              Blog & Insights
            </h2>
          </div>
          <div style={{ flexShrink: 0 }}>
            <img
              src="/assets/hero2.png"
              alt="Blog Banner"
              style={{ maxHeight: "220px", borderRadius: "8px" }}
            />
          </div>
        </div>

        {/* Blog Cards Grid */}
        <Row xs={1} md={3} className="g-4">
          {currentBlogs.map((b, idx) => (
            <Col key={b.id + "-" + idx}>
              <BlogCard blog={b} />
            </Col>
          ))}
        </Row>

        {/* Pagination */}
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "40px",
            marginBottom: "60px",
          }}
        >
          <Pagination>
            {/* Prev */}
            <Pagination.Prev
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 1}
            >
              ← Prev
            </Pagination.Prev>

            {/* Page numbers */}
            {Array.from({ length: totalPages }, (_, idx) => (
              <Pagination.Item
                key={idx + 1}
                active={idx + 1 === currentPage}
                onClick={() => handlePageChange(idx + 1)}
                style={{
                  backgroundColor: idx + 1 === currentPage ? "#154734" : "#fff",
                  color: idx + 1 === currentPage ? "#fff" : "#000",
                  border: "1px solid #dee2e6",
                }}
              >
                {idx + 1}
              </Pagination.Item>
            ))}

            {/* Next */}
            <Pagination.Next
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages}
              style={{
                backgroundColor: "#154734",
                color: "#fff",
                border: "none",
              }}
            >
              Next →
            </Pagination.Next>
          </Pagination>
        </div>
      </Container>
    </section>
  );
}
