import React from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';

const leaders = [
  {name: 'Abolaji Olorunkoya', title: 'Co-Founder & CEO', image: '/assets/leadership1.jpg', bio: 'A seasoned finance executive with 20+ years experience.'},
  {name: 'Jane Doe', title: 'COO', image: '/assets/leadership1.jpg', bio: 'Operations and supply chain lead.'}
];

export default function Leadership() {
  return (
    <section className="section" id="leadership">
      <Container className="container-narrow">
        <h2>Leadership & Governance</h2>
        <Row xs={1} md={2} className="g-4">
          {leaders.map((l, i) => (
            <Col key={i}>
              <Card className="h-100 shadow-sm">
                <Card.Img src={l.image} style={{height:240, objectFit:'cover'}} />
                <Card.Body>
                  <Card.Title>{l.name}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">{l.title}</Card.Subtitle>
                  <Card.Text>{l.bio}</Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
}
