import React from "react";

export default function BlogCard({ blog }) {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "flex-start",
        alignItems: "flex-start",
        width: "100%", // ✅ full responsive width
        height: "100%", // ✅ let content dictate height
        padding: "10px",
        boxSizing: "border-box",
        cursor: "pointer",
        transition: "transform 0.2s ease, box-shadow 0.2s ease",
      }}
      onMouseEnter={(e) => {
        e.currentTarget.style.transform = "translateY(-5px)";
        e.currentTarget.style.boxShadow = "0 8px 20px rgba(0,0,0,0.08)";
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.transform = "translateY(0)";
        e.currentTarget.style.boxShadow = "none";
      }}
    >
      {/* Image */}
      <div
        style={{
          width: "100%",
          aspectRatio: "4/3", // ✅ keeps consistent aspect ratio
          borderRadius: "8px",
          overflow: "hidden",
          marginBottom: "15px",
          background: "#D9D9D9",
          flexShrink: 0,
        }}
      >
        <img
          src={blog.image}
          alt={blog.title}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            display: "block",
          }}
        />
      </div>

      {/* Text container */}
      <div style={{ flexGrow: 1 }}>
        {/* Title */}
        <h3
          style={{
            color: "#222",
            fontFamily: "Raleway, sans-serif",
            fontSize: "1.25rem",
            fontWeight: 600,
            lineHeight: "1.4",
            margin: "0 0 10px",
          }}
        >
          {blog.title}
        </h3>

        {/* Excerpt */}
        <p
          style={{
            color: "#555",
            fontFamily: "Raleway, sans-serif",
            fontSize: "1rem",
            fontWeight: 400,
            lineHeight: "1.6",
            margin: 0,
          }}
        >
          {blog.excerpt}
        </p>
      </div>
    </div>
  );
}
