// src/components/NavbarMain.jsx
import React from "react";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import { useNavigate, useLocation } from "react-router-dom";
import logo from "../assets/logo.png";

export default function NavbarMain() {
  const navigate = useNavigate();
  const location = useLocation();

  // Scroll handler (for homepage sections only)
  const handleScrollTo = (e, id) => {
    e.preventDefault();
    const target = document.getElementById(id);
    if (location.pathname !== "/") {
      navigate("/");
      setTimeout(() => {
        document.getElementById(id)?.scrollIntoView({ behavior: "smooth" });
      }, 150);
    } else {
      target?.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <Navbar
      bg="white"
      expand="lg"
      style={{
        borderBottom: "none",
        boxShadow: "none",
        padding: "0",
        margin: "0", // ✅ remove margin
      }}
    >
      <Container
        fluid
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          padding: "0 64px", // ✅ spacing left/right only
          margin: "0",
        }}
      >
        {/* Logo */}
        <Navbar.Brand
          onClick={() => navigate("/")}
          style={{
            cursor: "pointer",
            display: "flex",
            alignItems: "center",
            flexShrink: 0,
          }}
        >
          <img
            src={logo}
            alt="Cavola Logo"
            style={{
              height: "140px", // ✅ Adjust logo size to match screenshot
              width: "auto",
              objectFit: "contain",
              display: "block",
            }}
          />
        </Navbar.Brand>

        {/* Hamburger Toggle (mobile) */}
        <Navbar.Toggle aria-controls="navbar-nav" />

        {/* Menu + Button */}
        <Navbar.Collapse
          id="navbar-nav"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Nav
            className="mx-auto"
            style={{
              display: "flex",
              alignItems: "center",
              gap: "64px",
              padding: "12px 40px",
              borderRadius: "30px",
              background: "#F3F3F3",
            }}
          >
            <Nav.Link
              onClick={(e) => handleScrollTo(e, "about")}
              style={{
                color: "#292121",
                fontSize: "18px",
                fontFamily: "Raleway",
              }}
            >
              About Us
            </Nav.Link>
            <Nav.Link
              onClick={(e) => handleScrollTo(e, "services")}
              style={{
                color: "#292121",
                fontSize: "18px",
                fontFamily: "Raleway",
              }}
            >
              Services
            </Nav.Link>
            <Nav.Link
              onClick={() => navigate("/blog")}
              style={{
                color: "#292121",
                fontSize: "18px",
                fontFamily: "Raleway",
              }}
            >
              Blog
            </Nav.Link>
            <Nav.Link
              onClick={() => navigate("/contact")}
              style={{
                color: "#292121",
                fontSize: "18px",
                fontFamily: "Raleway",
              }}
            >
              Contact Us
            </Nav.Link>
          </Nav>

          {/* CTA Button */}
          <div className="text-center ms-4">
            <Button
              style={{
                color: "#FFF",
                fontSize: "18px",
                fontFamily: "Raleway",
                display: "flex",
                width: "200px",
                padding: "10px 0",
                justifyContent: "center",
                alignItems: "center",
                borderRadius: "30px",
                background: "#86AD43",
                border: "none",
              }}
              onClick={() => navigate("/contact")}
            >
              Request a Quote
            </Button>
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
