// src/components/NavbarMain.jsx
import React from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { useNavigate, useLocation } from 'react-router-dom';
import logo from '../assets/logo.png';

export default function NavbarMain() {
  const navigate = useNavigate();
  const location = useLocation();

  // Scroll handler (for homepage sections only)
  const handleScrollTo = (e, id) => {
    e.preventDefault();
    const target = document.getElementById(id);
    if (location.pathname !== '/') {
      navigate('/');
      setTimeout(() => {
        document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
      }, 150);
    } else {
      target?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <Navbar
      bg="white"
      expand="lg" // ✅ collapses below lg (mobile/tablet)
      style={{
        borderBottom: 'none',
        boxShadow: 'none',
        padding: '0',
      }}
    >
      <Container
        fluid
        style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          padding: '0 20px', // smaller padding for mobile
        }}
      >
        {/* Logo */}
        <Navbar.Brand
          onClick={() => navigate('/')}
          style={{
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            flexShrink: 0,
          }}
        >
          <img
            src={logo}
            alt="Cavola Logo"
            style={{
              height: '120px', // ✅ desktop size
              width: 'auto',
              objectFit: 'contain',
              display: 'block',
            }}
            className="d-none d-md-block" // hide big logo on small screens
          />
          <img
            src={logo}
            alt="Cavola Logo Small"
            style={{
              height: '60px', // ✅ smaller logo for mobile
              width: 'auto',
              objectFit: 'contain',
              display: 'block',
            }}
            className="d-block d-md-none" // show only on mobile
          />
        </Navbar.Brand>

        {/* Hamburger Toggle (mobile) */}
        <Navbar.Toggle aria-controls="navbar-nav" />

        {/* Collapsible Menu */}
        <Navbar.Collapse id="navbar-nav" className="justify-content-center">
          <Nav
            className="mx-auto text-center"
            style={{
              display: 'flex',
              flexDirection: 'column',
              gap: '20px',
            }}
          >
            <Nav.Link
              onClick={(e) => handleScrollTo(e, 'about')}
              style={{ color: '#292121', fontSize: '20px', fontFamily: 'Raleway' }}
            >
              About Us
            </Nav.Link>
            <Nav.Link
              onClick={(e) => handleScrollTo(e, 'services')}
              style={{ color: '#292121', fontSize: '20px', fontFamily: 'Raleway' }}
            >
              Services
            </Nav.Link>
            <Nav.Link
              onClick={() => navigate('/blog')}
              style={{ color: '#292121', fontSize: '20px', fontFamily: 'Raleway' }}
            >
              Blog
            </Nav.Link>
            <Nav.Link
              onClick={() => navigate('/contact')}
              style={{ color: '#292121', fontSize: '20px', fontFamily: 'Raleway' }}
            >
              Contact Us
            </Nav.Link>

            {/* CTA Button inside collapse on mobile */}
            <div className="text-center mt-2">
              <Button
                style={{
                  color: '#FFF',
                  fontSize: '18px',
                  fontFamily: 'Raleway',
                  width: '200px',
                  borderRadius: '25px',
                  background: '#86AD43',
                  border: 'none',
                }}
                onClick={() => navigate('/contact')}
              >
                Request a Quote
              </Button>
            </div>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
