import React from 'react';
import { Carousel, Container, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

import hero1 from '../assets/hero1.png';
import hero2 from '../assets/hero2.png';
import hero3 from '../assets/hero3.png';
import hero4 from '../assets/hero4.png';
import hero5 from '../assets/hero5.png';

export default function Hero() {
  // Add captions for each image
  const images = [
    { src: hero1, caption: 'Chili Pepper' },
    { src: hero2, caption: 'Cashew Nut' },
    { src: hero3, caption: 'Sesame Seed' },
    { src: hero4, caption: 'Cocoa Bean' },
    { src: hero5, caption: 'Ginger Root' },
  ];

  const navigate = useNavigate();

  return (
    <section
      className="hero"
      id="home"
      style={{
        background: '#fff',
        padding: '40px 0 80px 0',
      }}
    >
      <Container className="d-flex align-items-center justify-content-between flex-wrap">
        {/* Left slideshow */}
        <div style={{ flex: 1, minWidth: 320, marginBottom: '20px' }}>
          <Carousel
            controls={false}
            indicators
            interval={4000}
            fade
            ride="carousel"
            pause={false}
          >
            {images.map((img, i) => (
              <Carousel.Item key={i}>
                <div style={{ textAlign: 'center' }}>
                  <img
                    src={img.src}
                    alt={img.caption}
                    style={{
                      width: '100%',
                      borderRadius: '12px',
                      objectFit: 'cover',
                      maxHeight: '680px',
                      display: 'block',
                      margin: '0 auto',
                    }}
                  />
                  <p
                    style={{
                      marginTop: '16px',
                      fontSize: '1.2rem',
                      fontWeight: 500,
                      color: '#7CB342',
                    }}
                  >
                    {img.caption}
                  </p>
                </div>
              </Carousel.Item>
            ))}
          </Carousel>
        </div>

        {/* Right text block */}
        <div style={{ flex: 1, minWidth: 320, paddingLeft: '40px' }}>
          <h1
            style={{
              fontFamily: 'Raleway, sans-serif',
              fontWeight: 700,
              lineHeight: '1.2',
              marginBottom: '20px',
              color: '#292121',
              textDecoration: 'none',
              border: 'none',
            }}
          >
            Finding{' '}
            <span style={{ color: '#7CB342' }}>
              reliable agro-commodity suppliers
            </span>{' '}
            in West Africa shouldnâ€™t be difficult.
          </h1>

          <p
            style={{
              fontSize: '1.05rem',
              color: '#333',
              marginBottom: '24px',
              lineHeight: '1.6',
            }}
          >
            Inconsistent quality, weak logistics, and compliance risks cost
            buyers time and money. Cavola solves this by delivering trusted,
            certified agro-commodities and petrochemicals from Nigeria to
            global markets.
          </p>

          <div style={{ display: 'flex', gap: '16px' }}>
            <Button
              style={{
                background: '#7CB342',
                border: 'none',
                borderRadius: '999px',
                padding: '10px 24px',
              }}
              onClick={() => navigate('/#contact')}
            >
              Request a Quote
            </Button>

            <Button
              variant="outline-dark"
              style={{
                borderRadius: '999px',
                padding: '10px 24px',
                borderWidth: '2px',
                fontWeight: 600,
              }}
              onClick={() => navigate('/#about')}
            >
              Book a Call with Us
            </Button>
          </div>
        </div>
      </Container>
    </section>
  );
}
