import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import ServiceCard from './ServiceCard';

const items = [
  {title: 'Export & Compliance', text: 'Full export documentation and NEPC compliance.'},
  {title: 'Quality Assurance', text: 'On-site quality checks and lab testing.'},
  {title: 'Logistics', text: 'End-to-end logistics from farm to port.'},
  {title: 'Market Access', text: 'Connecting producers to international buyers.'}
];

export default function Services() {
  return (
    <section className="section bg-light" id="services">
      <Container className="container-narrow">
        <h2>Products & Services</h2>
        <Row xs={1} md={2} className="g-4">
          {items.map((s, i) => (
            <Col key={i}><ServiceCard service={s} /></Col>
          ))}
        </Row>
      </Container>
    </section>
  );
}
