import React from 'react';
import { Container, Button } from 'react-bootstrap';
import leadershipImg from '../assets/leadership.jpg';

export default function Leadership() {
  return (
    <section
      id="leadership"
      style={{
        background: '#154734',
        width: '1729px',
        height: '809px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        position: 'relative',
      }}
    >
      <Container
        style={{
          maxWidth: '1600px',
          padding: '0 40px',
          margin: '0 auto',
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'flex-start',
          justifyContent: 'space-between',
          gap: '40px',
          position: 'relative',
        }}
      >
        {/* Left Side */}
        <div style={{ flex: 1, minWidth: '300px', position: 'relative' }}>
          {/* Leadership & Governance */}
          <div
            style={{
              color: '#FFF',
              fontFamily: 'Raleway, sans-serif',
              fontSize: '18.707px',
              fontWeight: 400,
              lineHeight: '24.943px',
              textTransform: 'uppercase',
              marginBottom: '20px',
            }}
          >
            LEADERSHIP & GOVERNANCE
          </div>

          {/* Built on Experience */}
          <h2
            style={{
              width: '479px',
              color: '#F7C35F',
              fontFamily: 'Raleway, sans-serif',
              fontSize: '50px',
              fontWeight: 700,
              lineHeight: '61.68px',
              marginBottom: '20px',
            }}
          >
            Built on experience
            <br />
            you can trust
          </h2>

          {/* Description */}
          <p
            style={{
              width: '551px',
              color: '#FFF',
              fontFamily: 'Raleway, sans-serif',
              fontSize: '20px',
              fontWeight: 400,
              lineHeight: '32px',
              marginBottom: '40px',
            }}
          >
            Our leadership team brings global experience in finance, agribusiness, and international trade.
          </p>
        </div>

        {/* Right Side - Image */}
        <div
          style={{
            width: '776px',
            height: '604px',
            borderRadius: '10px',
            background: `url(${leadershipImg}) lightgray 50% / cover no-repeat`,
            position: 'relative',
            flexShrink: 0,
          }}
        />
      </Container>

      {/* Spotlight Card (Overlapping Image) */}
      <div
        style={{
          background: '#fff',
          borderRadius: '12px',
          padding: '40px',
          boxShadow: '0 2px 12px rgba(0,0,0,0.06)',
          position: 'absolute',
          left: '80px', // adjust how far it sits from the left
          bottom: '60px', // adjust vertical overlap
          zIndex: 5,
          width: '700px',
        }}
      >
        {/* Spotlight */}
        <div
          style={{
            color: '#86AD43',
            fontFamily: 'Raleway, sans-serif',
            fontSize: '23.695px',
            fontWeight: 700,
            lineHeight: '29.23px',
            marginBottom: '12px',
          }}
        >
          Spotlight
        </div>

        {/* Abolaji + Role */}
        <div
          style={{
            fontFamily: 'Raleway, sans-serif',
            fontSize: '50px',
            fontWeight: 700,
            lineHeight: '61.68px',
            marginBottom: '16px',
            color: '#1E1E1E',
          }}
        >
          Abolaji Olorunkoya –{' '}
          <span
            style={{
              color: '#86AD43',
              fontFamily: 'Raleway, sans-serif',
              fontSize: '24px',
              fontWeight: 700,
              lineHeight: '61.68px',
            }}
          >
            Co-Founder & CEO
          </span>
        </div>

        {/* Description */}
        <div
          style={{
            color: '#1E1E1E',
            fontFamily: 'Raleway, sans-serif',
            fontSize: '20px',
            fontWeight: 400,
            lineHeight: '32px',
            marginBottom: '32px',
          }}
        >
          A seasoned finance executive with 20+ years of cross-continental experience, Abolaji bridges
          African farmers with global markets through ethical sourcing, innovation, and sustainable trade.
        </div>

        {/* Button */}
        <Button
          style={{
            display: 'inline-flex',
            padding: '5px 29px',
            justifyContent: 'center',
            alignItems: 'center',
            gap: '10px',
            borderRadius: '25.235px',
            border: '1px solid #094D12',
            background: '#fff',
            color: '#094D12',
            fontFamily: 'Raleway, sans-serif',
            fontSize: '22.918px',
            fontWeight: 700,
            lineHeight: '38.197px',
          }}
        >
          Connect With Us
        </Button>
      </div>
    </section>
  );
}
