import React from 'react';

export default function BlogCard({ blog }) {
  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        width: '449.98px',
      }}
    >
      {/* Image */}
      <div
        style={{
          width: '449.98px',
          height: '285.46px',
          borderRadius: '5.448px',
          overflow: 'hidden',
          marginBottom: '20px',
          background: '#D9D9D9',
        }}
      >
        <img
          src={blog.image}
          alt={blog.title}
          style={{
            width: '100%',
            height: '80%',
            objectFit: 'cover',
            display: 'block',
          }}
        />
      </div>

      {/* Title */}
      <h3
        style={{
          width: '448.891px',
          color: '#222',
          fontFamily: 'Raleway',
          fontSize: '29.418px',
          fontWeight: 600,
          lineHeight: '140%',
          margin: 0,
          marginBottom: '12px',
        }}
      >
        {blog.title}
      </h3>

      {/* Text */}
      <p
        style={{
          width: '371.533px',
          color: '#555',
          fontFamily: 'Raleway',
          fontSize: '16.343px',
          fontWeight: 400,
          lineHeight: '150%',
          margin: 0,
        }}
      >
        {blog.excerpt}
      </p>
    </div>
  );
}
