import React from 'react';
import Card from 'react-bootstrap/Card';

export default function BlogCard({ blog }) {
  return (
    <Card style={{ border: 'none', boxShadow: 'none', height: '100%' }}>
      {/* Blog Image with fixed height */}
      <div style={{ width: '100%', height: '220px', overflow: 'hidden', borderRadius: '8px', marginBottom: '16px' }}>
        <Card.Img
          src={blog.image}
          alt={blog.title}
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover', // ✅ crops nicely without stretching
          }}
        />
      </div>

      {/* Blog Title & Text */}
      <Card.Body style={{ padding: 0 }}>
        <Card.Title
          style={{
            fontSize: '1.1rem',
            fontWeight: 600,
            marginBottom: '8px',
          }}
        >
          {blog.title}
        </Card.Title>

        <Card.Text style={{ fontSize: '0.95rem', color: '#555' }}>
          {blog.excerpt}
        </Card.Text>
      </Card.Body>
    </Card>
  );
}
