import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import useStore from "../store/useStore";
import BlogCard from "./BlogCard";

// ✅ Import image directly from src/assets
import heroImage from "../assets/hero2.png";

export default function BlogPage() {
  const blogs = useStore((state) => state.blogs);

  return (
    <section style={{ background: "#fff", padding: "60px 0" }}>
      <Container style={{ maxWidth: "1200px" }}>
        {/* Blog Banner */}
        <div
          style={{
            background: "#154734",
            color: "#fff",
            borderRadius: "12px",
            marginBottom: "50px",
            padding: "40px",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            gap: "30px",
          }}
        >
          <div>
            <h2
              style={{
                fontSize: "2rem",
                fontWeight: "700",
                marginBottom: "10px",
              }}
            >
              Blog & Insights
            </h2>
          </div>
          <div style={{ flexShrink: 0 }}>
            <img
              src={heroImage} // ✅ imported image used here
              alt="Blog Banner"
              style={{ maxHeight: "160px", borderRadius: "8px" }}
            />
          </div>
        </div>

        {/* Blog Cards Grid */}
        <Row xs={1} md={3} className="g-4">
          {blogs.map((b) => (
            <Col key={b.id}>
              <BlogCard blog={b} />
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
}
