import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import ProductCard from './ProductCard';

const items = [
  {title: 'Ginger', text: 'Premium Nigerian ginger — dried and processed.', image: '/assets/product1.jpg'},
  {title: 'Sesame', text: 'High-oil sesame seeds, screened and graded.', image: '/assets/product2.jpg'},
  {title: 'Cocoa', text: 'Quality cocoa beans for international buyers.', image: '/assets/product1.jpg'},
  {title: 'Oil Palm', text: 'Sustainably sourced palm oil products.', image: '/assets/product2.jpg'},
];

export default function Products() {
  return (
    <section className="section bg-white" id="products">
      <Container className="container-narrow">
        <h2>Explore Our Products</h2>
        <Row xs={1} md={2} lg={4} className="g-4">
          {items.map((it, idx) => (
            <Col key={idx}>
              <ProductCard item={it} />
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
}
