import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';

// Import your icons/images
import nepcLogo from '../assets/nepc.png';
import farmersIcon from '../assets/farmers.png';
import flags from '../assets/flags.png'; // (UK + Nigeria combined flag image)

export default function Certifications() {
  const items = [
    {
      image: nepcLogo,
      text: 'NEPC Certified Exporter',
    },
    {
      image: farmersIcon,
      text: 'Direct sourcing from farmers',
    },
    {
      image: flags,
      text: 'Proven UK–Nigeria trade network',
    },
  ];

  return (
    <section
      style={{
        background: '#F7C35F',
        padding: '60px 20px', // ✅ no fixed px margins
      }}
    >
      <Container>
        <Row className="text-center g-4">
          {items.map((item, idx) => (
            <Col
              key={idx}
              xs={12}
              sm={6}
              md={4}
              className="d-flex flex-column align-items-center"
            >
              <img
                src={item.image}
                alt={item.text}
                style={{
                  maxWidth: '150px',
                  height: 'auto',
                  marginBottom: '16px',
                }}
              />
              <p
                style={{
                  color: '#094D12',
                  textAlign: 'center',
                  fontFamily: 'Raleway',
                  fontSize: '18px',
                  fontWeight: 700,
                  lineHeight: '1.6',
                  margin: 0,
                }}
              >
                {item.text}
              </p>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
}
