import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import wheatImg from '../assets/wheat-bg.png'; // wheat background image
import polyIcon from '../assets/poly.svg';   // arrow bullet svg

export default function GrowthJourney() {
  return (
    <section style={{ background: '#fff', padding: '80px 0' }}>
      <Container style={{ maxWidth: '1200px' }}>
        <Row className="align-items-center g-5">
          {/* Left Side - Wheat Background */}
          <Col md={6}>
            <div
              style={{
                width: '100%',
                aspectRatio: '1/1',
                background: `url(${wheatImg}) center/cover no-repeat`,
                position: 'relative',
                borderRadius: '12px',
                overflow: 'hidden',
              }}
            >
              <div
                style={{
                  position: 'absolute',
                  bottom: '30px',
                  left: '30px',
                  background: 'rgba(255, 255, 255, 0.70)',
                  height: '117px',
                  padding: '34px 49px',
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  borderRadius: '12px',
                }}
              >
                <Button
                  style={{
                    color: '#FFF',
                    fontFamily: 'Raleway',
                    fontSize: '22.918px',
                    fontWeight: 400,
                    lineHeight: '38.197px',
                    display: 'flex',
                    padding: '5px 29px',
                    justifyContent: 'center',
                    alignItems: 'center',
                    gap: '10px',
                    borderRadius: '25.235px',
                    border: '9px solid rgba(255, 255, 255, 0.57)',
                    background: '#094D12',
                  }}
                >
                  Discuss Partnership Opportunities â†’
                </Button>
              </div>
            </div>
          </Col>

          {/* Right Side - Solid Green Box */}
          <Col md={6}>
            <div
              style={{
                background: '#094D12',
                borderRadius: '12px',
                padding: '40px',
                color: '#FFF',
              }}
            >
              <h2
                style={{
                  color: '#FFF',
                  fontFamily: 'Raleway',
                  fontSize: '50px',
                  fontWeight: 700,
                  lineHeight: '61.68px',
                  marginBottom: '40px',
                }}
              >
                Our growth journey
              </h2>

              {/* Bullet Items */}
              {[
                {
                  title: 'Today',
                  text: 'Trading high-value crops and petrochemicals, sourced responsibly and delivered efficiently.',
                },
                {
                  title: 'Tomorrow',
                  text: 'Expanding into value-added processing â€” transforming raw crops into premium powders, oils, and extracts.',
                },
                {
                  title: 'Future',
                  text: 'Owning plantations to guarantee traceability, quality, and sustainability across the entire farm-to-market chain.',
                },
              ].map((item, i) => (
                <div
                  key={i}
                  style={{
                    display: 'flex',
                    flexDirection: 'column',
                    width: '100%',
                    padding: '32px 29px',
                    borderRadius: '10px',
                    border: '1px solid #FFF',
                    marginBottom: '20px',
                  }}
                >
                  <div
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      marginBottom: '10px',
                    }}
                  >
                    <img
                      src={polyIcon}
                      alt="bullet"
                      style={{
                        width: '18px',
                        height: '18px',
                        marginRight: '12px',
                      }}
                    />
                    <div
                      style={{
                        fontFamily: 'Raleway',
                        fontWeight: 600,
                        fontSize: '24px',
                        lineHeight: '32px',
                      }}
                    >
                      {item.title}
                    </div>
                  </div>
                  <div
                    style={{
                      fontSize: '18px',
                      lineHeight: '28px',
                      color: '#FFF',
                    }}
                  >
                    {item.text}
                  </div>
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
}
