import React from 'react';
import { Carousel, Container, Button } from 'react-bootstrap';
import hero1 from '../assets/hero1.jpg';
import hero2 from '../assets/hero2.jpg';
import hero3 from '../assets/hero3.jpg';
import { useNavigate } from 'react-router-dom';

export default function Hero() {
  const images = [hero1, hero2, hero3];
  const navigate = useNavigate();

  return (
    <section className="hero" id="home">
      <Container className="container-narrow">
        <Carousel>
          {images.map((img, i) => (
            <Carousel.Item key={i}>
              <div style={{display:'flex', alignItems:'center', gap:'40px', padding:'40px 0', flexDirection: 'row', flexWrap: 'wrap'}}>
                <img
                  src={img}
                  alt={`Slide ${i+1}`}
                  style={{width: '48%', borderRadius: 8, objectFit: 'cover', minHeight: 220}}
                />
                <div style={{flex: 1, minWidth: 260}}>
                  <h1>From Nigeria’s farms to international buyers</h1>
                  <p>
                    We ensure compliance, consistency, and efficiency — connecting premium agro-commodities to global partners.
                  </p>
                  <div style={{display:'flex', gap:12, marginTop:18}}>
                    <Button className="btn-cta" onClick={()=>navigate('/#contact')}>Request a Quote</Button>
                    <Button variant="outline-primary" onClick={()=>navigate('/#about')}>Learn More</Button>
                  </div>
                </div>
              </div>
            </Carousel.Item>
          ))}
        </Carousel>
      </Container>
    </section>
  );
}
