import React, {useState} from 'react';
import { Container, Form, Button } from 'react-bootstrap';

export default function Contact() {
  const [form, setForm] = useState({name:'', email:'', message:''});
  const handleChange = (e) => setForm({...form, [e.target.name]: e.target.value});
  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Message sent! (demo) — we are not yet connected to a backend.');
    setForm({name:'',email:'',message:''});
  };

  return (
    <section className="section bg-white" id="contact">
      <Container className="container-narrow">
        <h2>Let’s Talk Business</h2>
        <Form onSubmit={handleSubmit} style={{maxWidth:700}}>
          <Form.Group className="mb-3" controlId="name">
            <Form.Label>Full Name</Form.Label>
            <Form.Control name="name" value={form.name} onChange={handleChange} required />
          </Form.Group>
          <Form.Group className="mb-3" controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" name="email" value={form.email} onChange={handleChange} required />
          </Form.Group>
          <Form.Group className="mb-3" controlId="message">
            <Form.Label>Message</Form.Label>
            <Form.Control as="textarea" rows={5} name="message" value={form.message} onChange={handleChange} required />
          </Form.Group>
          <Button type="submit" className="btn-cta">Send Message</Button>
        </Form>
        <div style={{marginTop:24}}>
          <p><strong>Contact</strong></p>
          <p>Nigeria: +234 802 465 5655 | +234 907 473 4419</p>
          <p>UK: +44 7943 612728</p>
          <p>Email: cavola@cavolawestafrica.com</p>
        </div>
      </Container>
    </section>
  );
}
