import React from 'react';
import { Container, Button } from 'react-bootstrap';
import leadershipImg from '../assets/leadership.jpg';

export default function Leadership() {
  return (
    <section
      id="leadership"
      style={{
        background: '#154734',
        padding: '0',
        position: 'relative',
        width: '100%',
        overflow: 'hidden',
      }}
    >
      <Container
        style={{
          maxWidth: '1200px',
          padding: '0 20px',
          margin: '0 auto',
          position: 'relative',
        }}
      >
        <div
          style={{
            display: 'flex',
            flexDirection: 'row',
            justifyContent: 'space-between',
            alignItems: 'flex-start',
            width: '100%',
            flexWrap: 'wrap',
            position: 'relative',
          }}
        >
          {/* Left Side */}
          <div
            style={{
              flex: 1,
              padding: '0 20px',
              minWidth: '300px',
              marginBottom: '40px',
            }}
          >
            <div
              style={{
                marginBottom: '18px',
                color: '#FFF',
                fontSize: '18.7px',
                lineHeight: '24.94px',
                fontWeight: 400,
                fontFamily: 'Raleway',
                textTransform: 'uppercase',
              }}
            >
              Leadership & Governance
            </div>
            <h2
              style={{
                color: '#F7C35F',
                fontFamily: 'Raleway',
                fontSize: '50px',
                fontWeight: 700,
                lineHeight: '61.68px',
                marginBottom: '18px',
              }}
            >
              Built on experience <br /> you can trust
            </h2>
            <p
              style={{
                color: '#FFF',
                fontFamily: 'Raleway',
                fontSize: '20px',
                lineHeight: '32px',
                maxWidth: '551px',
              }}
            >
              Our leadership team brings global experience in finance, agribusiness, and international trade.
            </p>
          </div>

          {/* Right Side - Image */}
          <div
            style={{
              flex: 1,
              display: 'flex',
              justifyContent: 'flex-end',
              padding: '0 20px',
              minWidth: '300px',
              position: 'relative',
            }}
          >
            <div
              style={{
                width: '776px',
                height: '604px',
                borderRadius: '10px',
                background: `url(${leadershipImg}) lightgray 50% / cover no-repeat`,
              }}
            ></div>
          </div>

          {/* Spotlight Card - Positioned Overlapping */}
          <div
            style={{
              position: 'absolute',
              bottom: '-40px',
              left: '40px',
              background: '#fff',
              borderRadius: '12px',
              padding: '32px',
              boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
              width: '650px',
              zIndex: 5,
            }}
          >
            <div
              style={{
                color: '#86AD43',
                fontFamily: 'Raleway',
                fontSize: '23.7px',
                fontWeight: 700,
                lineHeight: '29.23px',
                marginBottom: '12px',
                height:'370px',
                width:'808px'
              }}
            >
              Spotlight
            </div>
            <div
              style={{
                color: '#1E1E1E',
                fontFamily: 'Raleway',
                fontSize: '50px',
                fontWeight: 700,
                lineHeight: '61.68px',
                marginBottom: '12px',
              }}
            >
              Abolaji Olorunkoya{' '}
              <span style={{ color: '#86AD43', fontSize: '24px', fontWeight: 700 }}>Co-Founder & CEO</span>
            </div>
            <div
              style={{
                color: '#1E1E1E',
                fontFamily: 'Raleway',
                fontSize: '20px',
                fontWeight: 400,
                lineHeight: '32px',
                marginBottom: '20px',
                maxWidth: '755px',
              }}
            >
              A seasoned finance executive with 20+ years of cross-continental experience, Abolaji bridges African
              farmers with global markets through ethical sourcing, innovation, and sustainable trade.
            </div>
            <Button
              style={{
                display: 'inline-flex',
                padding: '5px 29px',
                justifyContent: 'center',
                alignItems: 'center',
                borderRadius: '25px',
                border: '1px solid #094D12',
                background: '#fff',
                color: '#094D12',
                fontFamily: 'Raleway',
                fontSize: '22.9px',
                fontWeight: 700,
                lineHeight: '38.2px',
              }}
            >
              Connect With Us
            </Button>
          </div>
        </div>
      </Container>
    </section>
  );
}
