import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import useStore from "../store/useStore";
import BlogCard from "./BlogCard";
import bannerImg from "../assets/hero2.png"; // ✅ correct import

export default function BlogList() {
  const blogs = useStore((state) => state.blogs);
  const navigate = useNavigate();

  // Pagination setup
  const blogsPerPage = 6;
  const [currentPage, setCurrentPage] = useState(1);

  const indexOfLast = currentPage * blogsPerPage;
  const indexOfFirst = indexOfLast - blogsPerPage;
  const currentBlogs = blogs.slice(indexOfFirst, indexOfLast);

  const totalPages = Math.ceil(blogs.length / blogsPerPage);

  return (
    <>
      {/* Header Section */}
      <section
        style={{
          width: "100%",
          minHeight: "712px",
          borderRadius: "10px",
          background: "#094D12",
          margin: "0 auto",
          padding: "40px",
          color: "#fff",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          flexWrap: "wrap",
        }}
      >
        {/* Title */}
        <h2
          style={{
            maxWidth: "519px",
            color: "#F7C35F",
            fontFamily: "Raleway",
            fontSize: "64px",
            fontWeight: 700,
            lineHeight: "61.68px", // ✅ 96.374%
            margin: 0,
          }}
        >
          Blog & Insights
        </h2>

        {/* Banner image */}
        <div
          style={{
            width: "885.912px",
            height: "809px",
            background: `url(${bannerImg}) / cover no-repeat`,
            borderRadius: "10px",
            flexShrink: 0,
            marginTop: "20px",
          }}
        />
      </section>

      {/* Blog Grid Section */}
      <section style={{ background: "#fff", padding: "60px 0" }}>
        <Container>
          <Row xs={1} md={3} className="g-4">
            {currentBlogs.map((blog) => (
              <Col key={blog.id} onClick={() => navigate(`/blog/${blog.id}`)}>
                <BlogCard blog={blog} />
              </Col>
            ))}
          </Row>

          {/* Pagination */}
          <div
            style={{
              marginTop: "40px",
              display: "flex",
              justifyContent: "center",
              gap: "10px",
              flexWrap: "wrap",
            }}
          >
            {/* Prev */}
            <button
              onClick={() => setCurrentPage(currentPage - 1)}
              disabled={currentPage === 1}
              style={{
                width: "122.676px",
                height: "47.902px",
                borderRadius: "5.842px",
                border: "1.168px solid #C4C4C4",
                background: "transparent",
                color: "#000",
                cursor: currentPage === 1 ? "not-allowed" : "pointer",
              }}
            >
              Prev
            </button>

            {/* Numbers */}
            {[...Array(totalPages).keys()].map((num) => (
              <button
                key={num + 1}
                onClick={() => setCurrentPage(num + 1)}
                style={{
                  width: "47.902px",
                  height: "47.902px",
                  borderRadius: "5.842px",
                  border: "1.168px solid #C4C4C4",
                  background:
                    currentPage === num + 1 ? "#246A21" : "transparent",
                  color: currentPage === num + 1 ? "#fff" : "#000",
                  cursor: "pointer",
                }}
              >
                {num + 1}
              </button>
            ))}

            {/* Next */}
            <button
              onClick={() => setCurrentPage(currentPage + 1)}
              disabled={currentPage === totalPages}
              style={{
                width: "120.339px",
                height: "47.902px",
                borderRadius: "5.842px",
                background: "#246A21",
                color: "#fff",
                border: "none",
                cursor: currentPage === totalPages ? "not-allowed" : "pointer",
              }}
            >
              Next
            </button>
          </div>
        </Container>
      </section>
    </>
  );
}
