import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import useStore from "../store/useStore";
import BlogCard from "./BlogCard";
import bannerImg from "../assets/hero2.png";

export default function BlogList() {
  const blogs = useStore((state) => state.blogs);
  const navigate = useNavigate();

  // ✅ Take first row (3 blogs max)
  const firstRow = blogs.slice(0, 3);

  // ✅ Duplicate that row 3 times
  const duplicatedBlogs = [...firstRow, ...firstRow, ...firstRow];

  return (
    <section style={{ width: "100%", margin: "0 auto", padding: "40px 0" }}>
      <Container fluid>
        {/* ✅ Header Section */}
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            flexWrap: "wrap",
            borderRadius: "10px",
            background: "#094D12",
            padding: "40px",
            height:"712px",
            color: "#fff",
          }}
        >
          <h2
            style={{
              color: "#F7C35F",
              fontFamily: "Raleway",
              fontSize: "64px",
              fontWeight: 700,
              lineHeight: "61.68px",
              margin: 0,
              textAlign:"center",
            }}
          >
            Blog & Insights
          </h2>

          <div
            style={{
              width: "100%",
              maxWidth: "885px",
              height: "400px",
              background: `url(${bannerImg}) center / cover no-repeat`,
              borderRadius: "10px",
              marginTop: "20px",
            }}
          />
        </div>

        {/* ✅ Blog Grid - Always 9 blogs (3x3) */}
        <Row xs={1} sm={2} md={3} className="g-4 mt-5">
          {duplicatedBlogs.map((blog, index) => (
            <Col key={index} style={{ display: "flex" }}>
              <div
                style={{ flex: 1, cursor: "pointer" }}
                onClick={() => navigate(`/blog/${blog.id}`)}
              >
                <BlogCard blog={blog} />
              </div>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
}
