import React from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { useNavigate, useLocation } from 'react-router-dom';

export default function NavbarMain() {
  const navigate = useNavigate();
  const location = useLocation();

  const handleScroll = (e, id) => {
    e.preventDefault();
    const target = document.getElementById(id);
    if (location.pathname !== '/') {
      navigate('/');
      // wait for navigation then scroll
      setTimeout(() => {
        document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
      }, 150);
    } else {
      target?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <Navbar bg="light" expand="lg" fixed="top" className="shadow-sm">
      <Container>
        <Navbar.Brand href="/" className="header-brand">Cavola West Africa</Navbar.Brand>
        <Navbar.Toggle aria-controls="main-nav" />
        <Navbar.Collapse id="main-nav">
          <Nav className="ms-auto">
            <Nav.Link href="#about" onClick={(e)=>handleScroll(e,'about')}>About</Nav.Link>
            <Nav.Link href="#products" onClick={(e)=>handleScroll(e,'products')}>Products</Nav.Link>
            <Nav.Link href="#services" onClick={(e)=>handleScroll(e,'services')}>Services</Nav.Link>
            <Nav.Link href="#leadership" onClick={(e)=>handleScroll(e,'leadership')}>Leadership</Nav.Link>
            <Nav.Link href="#blog" onClick={(e)=>handleScroll(e,'blog')}>Blog</Nav.Link>
            <Nav.Link href="#contact" onClick={(e)=>handleScroll(e,'contact')}>Contact</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
