import React from 'react';
import { Container, Button } from 'react-bootstrap';
import leadershipImg from '../assets/leadership.jpg';

export default function Leadership() {
  return (
    <section id="leadership" style={{ background: '#154734', padding: '0', minHeight: '700px' }}>
      <Container fluid style={{ padding: 0 }}>
        <div
          style={{
            display: 'flex',
            alignItems: 'flex-start',
            justifyContent: 'space-between',
            minHeight: '700px',
          }}
        >
          {/* Left Side */}
          <div style={{ flex: 1, padding: '64px 0 0 80px' }}>
            <div
              style={{
                marginBottom: '18px',
                color: '#fff',
                fontSize: '0.95rem',
                textTransform: 'uppercase',
                letterSpacing: '1px',
              }}
            >
              LEADERSHIP & GOVERNANCE
            </div>
            <h2
              style={{
                color: '#FFC94B',
                fontWeight: 700,
                fontSize: '2.6rem',
                marginBottom: '18px',
                lineHeight: '1.15',
              }}
            >
              Built on experience
              <br />
              you can trust
            </h2>
            <p
              style={{
                color: '#fff',
                fontSize: '1.08rem',
                marginBottom: '40px',
                maxWidth: '500px',
              }}
            >
              Our leadership team brings global experience in finance, agribusiness, and international trade.
            </p>

            {/* Spotlight Card */}
            <div
              style={{
                background: '#fff',
                borderRadius: '12px',
                padding: '32px 36px',
                boxShadow: '0 2px 12px rgba(0,0,0,0.04)',
                maxWidth: '600px',
              }}
            >
              <div style={{ color: '#7CB342', fontWeight: 600, fontSize: '1.1rem', marginBottom: '10px' }}>
                Spotlight
              </div>
              <div style={{ fontWeight: 700, fontSize: '2rem', color: '#154734', marginBottom: '8px' }}>
                Abolaji Olorunkoya <span style={{ fontWeight: 400, color: '#154734' }}>–</span>
                <span
                  style={{
                    color: '#7CB342',
                    fontWeight: 600,
                    fontSize: '1.1rem',
                    marginLeft: '8px',
                  }}
                >
                  Co-Founder & CEO
                </span>
              </div>
              <div style={{ color: '#666', fontSize: '1.08rem', marginBottom: '24px' }}>
                A seasoned finance executive with 20+ years of cross-continental experience, Abolaji bridges African farmers with global markets through ethical sourcing, innovation, and sustainable trade.
              </div>
              <Button
                style={{
                  background: '#fff',
                  color: '#154734',
                  border: '2px solid #154734',
                  borderRadius: '999px',
                  padding: '10px 28px',
                  fontWeight: 600,
                  fontSize: '1rem',
                  boxShadow: 'none',
                }}
              >
                Connect With Us
              </Button>
            </div>
          </div>

          {/* Right Side - Image */}
          <div
            style={{
              flex: 1,
              display: 'flex',
              alignItems: 'flex-start',
              justifyContent: 'flex-end',
              padding: '64px 80px 0 0',
            }}
          >
            <div
              style={{
                background: '#fff',
                borderRadius: '16px',
                overflow: 'hidden',
                width: '100%',
                maxWidth: '520px',
                height: '370px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
              }}
            >
              <img
                src={leadershipImg}
                alt="Abolaji Olorunkoya"
                style={{
                  width: '100%',
                  height: '100%',
                  objectFit: 'cover',
                }}
              />
            </div>
          </div>
        </div>
      </Container>
    </section>
  );
}
