import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';

// Import your icons/images
import nepcLogo from '../assets/nepc.png';
import farmersIcon from '../assets/farmers.png';
import flags from '../assets/flags.png'; // (UK + Nigeria combined flag image)

export default function Certifications() {
  const items = [
    {
      image: nepcLogo,
      text: 'NEPC Certified Exporter',
      style: {
        height: '94.657px',
        aspectRatio: '316.15 / 94.66',
        alignSelf: 'stretch',
        objectFit: 'contain',
      },
    },
    {
      image: farmersIcon,
      text: 'Direct sourcing from farmers',
      style: {
        width: '169.945px',
        height: '126.626px',
        aspectRatio: '169.94 / 126.63',
        flexShrink: 0,
        objectFit: 'cover',
      },
    },
    {
      image: flags,
      text: 'Proven UK–Nigeria trade network',
      style: {
        height: '94.657px',
        objectFit: 'contain',
      },
    },
  ];

  return (
    <section
      style={{
        display: 'flex',
        padding: '20.101px 137.5px 28.398px 137px',
        justifyContent: 'center',
        alignItems: 'center',
        background: '#F7C35F',
      }}
    >
      <Container>
        <Row className="text-center">
          {items.map((item, idx) => (
            <Col md={4} key={idx} className="mb-4 mb-md-0 d-flex flex-column align-items-center">
              <img
                src={item.image}
                alt={item.text}
                style={item.style}
              />
              <p
                style={{
                  color: '#094D12',
                  textAlign: 'center',
                  fontFamily: 'Raleway, sans-serif',
                  fontSize: '18.988px',
                  fontStyle: 'normal',
                  fontWeight: 700,
                  lineHeight: '36.265px', // 190.987%
                  marginTop: '12px',
                  marginBottom: 0,
                }}
              >
                {item.text}
              </p>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
}
