import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { useNavigate, useLocation } from 'react-router-dom';
import logo from '../assets/logo.png';

export default function NavbarMain() {
  const navigate = useNavigate();
  const location = useLocation();

  const [showNavbar, setShowNavbar] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);

  // Handle scroll direction
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > lastScrollY && window.scrollY > 100) {
        // scrolling down
        setShowNavbar(false);
      } else {
        // scrolling up
        setShowNavbar(true);
      }
      setLastScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  const handleScrollTo = (e, id) => {
    e.preventDefault();
    const target = document.getElementById(id);
    if (location.pathname !== '/') {
      navigate('/');
      setTimeout(() => {
        document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
      }, 150);
    } else {
      target?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <Navbar
      bg="white"
      expand="lg"
      fixed="top"
      className="py-2 transition-navbar"
      style={{
        borderBottom: 'none',
        boxShadow: 'none',
        transform: showNavbar ? 'translateY(0)' : 'translateY(-100%)',
        transition: 'transform 0.4s ease-in-out',
      }}
    >
      <Container
        fluid
        className="d-flex justify-content-between align-items-center"
        style={{ paddingLeft: '20px', paddingRight: '40px' }}
      >
        {/* Logo */}
        <Navbar.Brand href="/" className="d-flex align-items-center">
          <img
            src={logo}
            alt="Cavola Logo"
            style={{ height: '120px', marginRight: '8px' }}
          />
        </Navbar.Brand>

        {/* Centered Nav Menu */}
        <div
          className="d-none d-lg-flex px-4 py-2"
          style={{
            background: '#f8f8f8',
            borderRadius: '999px',
          }}
        >
          <Nav className="mx-auto">
            <Nav.Link href="#about" onClick={(e) => handleScrollTo(e, 'about')} className="px-3">
              About Us
            </Nav.Link>
            <Nav.Link href="#services" onClick={(e) => handleScrollTo(e, 'services')} className="px-3">
              Services
            </Nav.Link>
            <Nav.Link href="#blog" onClick={(e) => handleScrollTo(e, 'blog')} className="px-3">
              Blog
            </Nav.Link>
            <Nav.Link href="#contact" onClick={(e) => handleScrollTo(e, 'contact')} className="px-3">
              Contact Us
            </Nav.Link>
          </Nav>
        </div>

        {/* Right CTA */}
        <Button
          className="d-none d-lg-inline-block"
          style={{
            background: '#7CB342',
            border: 'none',
            borderRadius: '999px',
            padding: '8px 20px',
          }}
          onClick={() => handleScrollTo(new Event('click'), 'contact')}
        >
          Request a Quote
        </Button>
      </Container>
    </Navbar>
  );
}
